        map[i] = surahStartPages[i]!;
      } else if (i > 1) {
        map[i] = map[i - 1]!;
      } else {
        map[i] = 1;
      }
    }
    return map;
  }

  static Map<String, dynamic> getSurahInfoFromPage(int pageNumber) {
    int surahNum = pageToDisplaySurah[pageNumber.clamp(1, 604)] ?? 1;
    return {
      'surahNumber': surahNum,
      'englishName': surahNames[surahNum]!['englishName']!,
      'arabicName': surahNames[surahNum]!['arabicName']!,
    };
  }

  // The missing function that caused the error
  static Map<String, dynamic> getJuzInfo(int juzNumber) {
    final info = juzStartInfo[juzNumber]!;
    final surahNum = info['surah']!;
    final surahInfo = surahNames[surahNum]!;
    return {
      'surahName': surahInfo['englishName'],
      'ayahNumber': info['ayah']
    };
  }
}
